package ifstmnt;

public class switchc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

				char C='G';
				
				switch(C)
				{
				case 99:System.out.println("sun");
				break;
				case 66:System.out.println("mon");
				break;
				case 67:System.out.println("tues");
				break;
				case 68:System.out.println("thurs");
				break;
		        case 69:System.out.println("fri");
		        break;
		        case 70:System.out.println("sat");
		        break;
		        case 71:System.out.println("wed");
		        break;
				default:System.out.println("invalid");
		     	}
	}
		}
		
